import { useEffect, useState } from "react";
import { NavLink, useNavigate } from "react-router-dom";
import FollowerModel from "../../../Models/FollowerModel";
import VacationModel from "../../../Models/VacationModel";
import { VacationsActionType, vacationsStore } from "../../../Redux/VacationsState";
import authService from "../../../Services/AuthService";
import notifyService from "../../../Services/NotifyService";
import vacationsService from "../../../Services/VacationsService";
import appConfig from "../../../Utils/Config";
import useVerifyLoggedIn from "../../../Utils/useVerifyLoggedIn";
import "./VacationCard.css";

interface VacationCardProps {
  vacation: VacationModel;
}

function VacationCard(props: VacationCardProps): JSX.Element {

    const [followers, setFollowers] = useState(props.vacation.followersCount);
    const [isFollowing, setIsFollowing] = useState(props.vacation.isFollowing);
    const [userId, setUserId] = useState<number>();

    useEffect(() => {

            authService.getUserIdFromToken()
            .then(id => {setUserId(id)})
            .catch(err => notifyService.error(err));

    }, []);


  async function click() {
    try {

            const vacationId = props.vacation.vacationId;
            const userId = await authService.getUserIdFromToken();

            const follower = new FollowerModel();
            follower.vacationId = vacationId;
            follower.userId = userId;

        if (isFollowing) {
            vacationsService.deleteFollower(follower);
            vacationsStore.dispatch({ type: VacationsActionType.isNotFollowing, payload: vacationId });
            setIsFollowing(props.vacation.isFollowing);
            vacationsStore.dispatch({ type: VacationsActionType.AddFollower, payload: vacationId });
            setFollowers(props.vacation.followersCount)
         } else {
            vacationsStore.dispatch({ type: VacationsActionType.isNowFollowing, payload: vacationId });
            setIsFollowing(props.vacation.isFollowing)
            vacationsStore.dispatch({ type: VacationsActionType.DeleteFollower, payload: vacationId });
            setFollowers(props.v)
            vacationsService.addFollower(follower);
        }

    } catch (err: any) {
      notifyService.error(err);
    }
  }

  return (
    <div className="VacationCard Box font-link">
      <label className="container" >
        <input
          type="checkbox"
          disabled={userId===1 || userId===0? true : false}
          checked={props.vacation.isFollowing === 1 ? true : false}
          onChange={() => click()}
        />
        <div className="checkmark"></div>
      </label>
      <div className="Likes">{props.vacation.followersCount}</div>
      <div className="Container">
        <h2>{props.vacation.destination}</h2>
        <br />
        <span>{props.vacation.description}</span>
        <br />
      </div>
      <div>
        <NavLink to={"/vacations/details/" + props.vacation.vacationId}>
          <img src={appConfig.vacationImagesUrl + props.vacation.imageName} />
        </NavLink>
      </div>
    </div>
  );
}

export default VacationCard;
